INSERT INTO luffycity.course_teacher (id, name, brief, avatar) VALUES (3, 'Jason Zhao', '对各种高精尖算法有较深研究，擅长C、C++、Python、Java语言、机器学习 2015年“甲骨文杯”全国Java程序设计大赛二等奖。计算机专业知识相当扎实，跟二进制一样严谨，擅长将枯燥的专业知识生动形象的表达出来，深入浅出，深受学员喜爱。', 'avatars/博士3x_1517450125.4485447.png');
